export default function validation (userData){

    const regexEmail=/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/

    const regexPassword=/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,10}$/

    const errors={}

    (!userData.email)? Errors.email='Email is required':Errors.email='';

    (userData.email.length > 35)? Errors.email='Email must cant be more than 35 characters':Errors.email='';

    (!regexEmail.test(userData.email))? Errors.email='Email is invalid':Errors.email='';

    (!regexPassword.test(userData.password))? Errors.password='Password is invalid':Errors.email='';

    return Errors

}
