import axios from "axios"
import { useParams } from "react-router-dom"
import { useState, useEffect } from "react"



export default function Deatil (){ 
    const {id}=useParams();
    const [character,setCharacter]=useState({});
//     useEffect(() => {
//         axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
//            if (data.name) {
//               setCharacter(data);
//            } else {
//               window.alert('No hay personajes con ese ID');
//            }
//         });
//         return setCharacter({});
//      }, [id]);
//     return(

//         <div>
//   {character.name && (
//     <div>
//       <h2>{character.name}</h2>
//       <p><b>Status:</b> {character.status}</p>
//       <p><b>Species:</b> {character.species}</p>
//       <p><b>Gender:</b> {character.gender}</p>
//       <p><b>Origin:</b> {character.origin.name}</p>
//       <img src={character.image} alt='Image Not Found' />
//     </div>
//   )}
// </div>
//     )



///////////////////////////////////////////////////////////////////////////

useEffect(() => {
   axios(`https://rickandmortyapi.com/api/character/${id}`)
     .then(({ data }) => {
       if (data.name) {
         setCharacter(data);
       } else {
         window.alert('No hay personajes con ese ID');
       }
     })
     .catch((error) => {
       console.error("Error al obtener los datos del personaje:", error);
       window.alert('Error al cargar los datos del personaje');
     });
 }, [id]);

 return (
   <div>
     {character.name && (
       <div>
         <h2>{character.name}</h2>
         {character.status && <p><b>Status:</b> {character.status}</p>}
         {character.species && <p><b>Species:</b> {character.species}</p>}
         {character.gender && <p><b>Gender:</b> {character.gender}</p>}
         {character.origin && character.origin.name && (
           <p><b>Origin:</b> {character.origin.name}</p>
         )}
         {character.image && (
           <img src={character.image} alt='Image Not Found' />
         )}
       </div>
     )}
   </div>
 )


};
