const initialState={myFavorites:[]}

const reducer=(state=initialState,actions)=>{

    switch(actions.type){

        case "ADD_FAV":
            return {
                ...state,myFavorites:[...state.myFavorites,actions.payload]
            }

            case REMOVE_FAV:
                return{
                    ...state,myFavorites:state.myFavorites.filter((element)=>element.id !== actions.payload)
                }

                default:state
    }


}

export default reducer
