    return <div className={styles.FlexContainer}>
       {favorites.map(fav =>
          <Card
             id={fav.id}
             name={fav.name}
             status={fav.status}
             species={fav.species}
             gender={fav.gender}
             origin={fav.origin.name}
             image={fav.image}
             onClose={()=>onClose(fav.id)}//llamamos a la funcion
          />)}
    </div>;
 }


function mapStateToProps(estado){

    return {favorites:estado.myFavorites}

}
export default connect(mapStateToProps,null)(Favorites)
