const initialState = {
    myFavorites: [],
    allFavorites:[]
}

const reducer = (state = initialState, actions) => {
    switch (actions.type) {
        case 'ADD_FAV':
            return {
                ...state,
                myFavorites: [...state.myFavorites, actions.payload],
                allFavorites: [...state.allFavorites, actions.payload]
            }
        case 'REMOVE_FAV':
            return {
                ...state,
                myFavorites: state.myFavorites.filter((fav) => fav.id !== actions.payload)
            }
         case 'FILTER_FAV':
            const favorit=state.allFavorites.filter((fav)=>fav.gender === actions.payload)
            return{
                ...state,
                myFavorites:actions.payload === 'allFavorites' ? [...state.allFavorites]:favorit
                



            }
        //     let favoriteFiltered=action.payload === 'All'? state.allFavorites : state.allFavorites.filter()
            
         case 'ORDER_FAV': 

         const orden=[...state.myFavorites]
         orden.sort((a, b)=>{
            if(actions.payload === 'A'){// los personajes se ordenan de menor a mayor
              return a.id - b.id
            }else{
                return b.id - a.id
            }
         })
            return{
                ...state,
                myFavorites:orden
                
            }


        
        default:
            return state
    }
}

export default reducer
